// src/components/Sidebar.jsx
import React, { useEffect, useState } from "react";
 import "./Style.css";

export default function Sidebar() {
  const [allHistory, setAllHistory] = useState([]);

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const resp = await fetch("http://localhost:5000/api/history");
        const data = await resp.json();
        setAllHistory(data);
      } catch (err) {
        console.error("History fetch error:", err);
      }
    };
    fetchHistory();
  }, []);

  return (
    <div className="sidebar">
      <h3>üìú History</h3>
      {allHistory.length === 0 && <div className="placeholder">No history yet</div>}
      <ul>
        {allHistory.map((h, i) => (
          <li key={i} className="history-item">
            <b>{h.type === "image" ? "üñºÔ∏è" : "üí¨"}</b> {h.userMessage}
            <div className="history-reply">‚Ü≥ {h.botReply}</div>
          </li>
        ))}
      </ul>
    </div>
  );
}
