// src/pages/Home.jsx
import React from "react";
import { useNavigate } from "react-router-dom";

export default function Home() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const goToChat = () => {
    if (token) {
      navigate("/chat");
    } else {
      navigate("/login");
    }
  };

  return (
    <div style={styles.container}>
      <h1>ðŸ‘‹ Welcome to Devbot</h1>
      <p>Your AI assistant for text & image-based conversations.</p>

      <div style={styles.buttonContainer}>
        <button onClick={() => navigate("/login")} style={styles.btn}>
          Login
        </button>
        <button onClick={() => navigate("/signup")} style={styles.btn}>
          Signup
        </button>
        <button onClick={goToChat} style={styles.chatBtn}>
          Go to Chat
        </button>
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: "40px",
    textAlign: "center",
  },
  buttonContainer: {
    marginTop: "30px",
    display: "flex",
    gap: "15px",
    justifyContent: "center",
  },
  btn: {
    padding: "10px 20px",
    fontSize: "16px",
    backgroundColor: "#ccc",
    border: "none",
    cursor: "pointer",
  },
  chatBtn: {
    padding: "10px 20px",
    fontSize: "16px",
    backgroundColor: "#4CAF50",
    color: "#fff",
    border: "none",
    cursor: "pointer",
  },
};
