// src/components/Chat.jsx
import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "./Sidebar";
import "./Style.css";

export default function Chat() {
  const [input, setInput] = useState("");
  const [history, setHistory] = useState([]);   // History of current user
  const [loading, setLoading] = useState(false);
  const [image, setImage] = useState(null);
  const fileInputRef = useRef();
  const navigate = useNavigate();

  const token = localStorage.getItem("token");

  // Redirect to login if no token
  useEffect(() => {
    if (!token) {
      navigate("/login");
    }
  }, [token, navigate]);

  // Fetch user-specific chat history on mount
  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const resp = await fetch("http://localhost:5000/api/history", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!resp.ok) {
          throw new Error("Failed to fetch history");
        }

        const data = await resp.json();
        console.log("History fetched:", data);

        // Assuming API returns an array of messages
        setHistory(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error("Error fetching history:", err);
        setHistory([]);
      }
    };

    if (token) {
      fetchHistory();
    }
  }, [token]);

  // Send message or image
  const send = async () => {
    if (!input.trim() && !image) return;

    const userMessage = { role: "user", text: input.trim(), image };
    setHistory((h) => [...h, userMessage]);

    setInput("");
    setImage(null);
    setLoading(true);

    try {
      if (image) {
        const formData = new FormData();
        formData.append("image", image);
        formData.append("prompt", input || "Describe this image");

        const resp = await fetch("http://localhost:5000/api/upload", {
          method: "POST",
          headers: {
            Authorization: `Bearer ${token}`,
          },
          body: formData,
        });

        const json = await resp.json();
        const reply = json.reply || "‚ö†Ô∏è No response";

        setHistory((h) => [...h, { role: "assistant", text: reply }]);
      } else {
        const body = {
          messages: [{ role: "user", parts: [{ text: userMessage.text }] }],
          prompt: userMessage.text,
        };

        const resp = await fetch("http://localhost:5000/api/chat", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(body),
        });

        const json = await resp.json();
        const reply = json.reply || "‚ö†Ô∏è No response";

        setHistory((h) => [...h, { role: "assistant", text: reply }]);
      }
    } catch (err) {
      setHistory((h) => [
        ...h,
        { role: "assistant", text: "‚ùå Error: " + err.message },
      ]);
    } finally {
      setLoading(false);
    }
  };

  // Handle Enter key for sending
  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      send();
    }
  };

  // Logout user
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="app-container">
      {/* Pass history to Sidebar if it needs */}
      <Sidebar history={history} />

      <div className="chat-container">
        <div className="chat-header">
          <h2 className="title">Devbot</h2>
          <button onClick={handleLogout} className="logout-btn">
            üö™ Logout
          </button>
        </div>

        <div className="chat-box">
          {history.length === 0 && <div className="placeholder">Say hi üëã</div>}
          {history.map((m, i) => (
            <div key={i} className={`message ${m.role}`}>
              {/* Show image if present */}
              {m.image && typeof m.image === "string" && (
                <img src={m.image} alt="upload" className="chat-image" />
              )}
              {m.image && m.image instanceof File && (
                <img
                  src={URL.createObjectURL(m.image)}
                  alt="upload"
                  className="chat-image"
                />
              )}
              <div className="bubble">{m.text || m.userMessage || m.botReply}</div>
            </div>
          ))}
          {loading && <div className="loading">‚è≥ Thinking...</div>}
        </div>

        <div className="input-area">
          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Type a message..."
          />
          <div className="actions">
            <button
              className="upload-btn"
              onClick={() => fileInputRef.current.click()}
            >
              üìé
            </button>
            <input
              type="file"
              ref={fileInputRef}
              style={{ display: "none" }}
              accept="image/*"
              onChange={(e) => setImage(e.target.files[0])}
            />
            <button className="send-btn" onClick={send} disabled={loading}>
              {loading ? "..." : "‚û§"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
