// Sidebar.jsx
import React from "react";
import { useNavigate } from "react-router-dom";
import "./Style.css";

export default function Sidebar({ allHistory }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login", { replace: true });
  };

  return (
    <div className="sidebar">
      <h3 className="sidebar-title">ðŸ“œ History</h3>

      <div className="sidebar-list">
        {allHistory.length === 0 ? (
          <p className="no-history">No chats yet</p>
        ) : (
          allHistory.map((msg, i) => (
            <div key={i} className={`sidebar-item ${msg.role}`}>
              {msg.image ? (
                <span>ðŸ“Ž Image</span>
              ) : (
                <span>
                  {msg.text && msg.text.length > 25
                    ? msg.text.substring(0, 25) + "..."
                    : msg.text}
                </span>
              )}
            </div>
          ))
        )}
      </div>

      {/* âœ… Logout button at bottom */}
      <div className="sidebar-footer">
        <button onClick={handleLogout} className="logout-btn">
          ðŸšª Logout
        </button>
      </div>
    </div>
  );
}
