import React, { useState, useEffect, useRef } from "react";
import "./App.css";

export default function App() {
  const [input, setInput] = useState("");
  const [history, setHistory] = useState([]); // Current session chat
  const [loading, setLoading] = useState(false);
  const [image, setImage] = useState(null);
  const [allHistory, setAllHistory] = useState([]); // DB se purani history
  const fileInputRef = useRef();

  // 🔹 Fetch history from backend
  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const resp = await fetch("http://localhost:5000/api/history");
        const data = await resp.json();
        setAllHistory(data);
      } catch (err) {
        console.error("History fetch error:", err);
      }
    };
    fetchHistory();
  }, []);

  const send = async () => {
    if (!input.trim() && !image) return;

    const userMessage = { role: "user", text: input.trim(), image };
    setHistory((h) => [...h, userMessage]);

    setInput("");
    setImage(null);
    setLoading(true);

    try {
      if (image) {
        // 🔹 If image, use upload API
        const formData = new FormData();
        formData.append("image", image);
        formData.append("prompt", input || "Describe this image");

        const resp = await fetch("http://localhost:5000/api/upload", {
          method: "POST",
          body: formData,
        });

        const json = await resp.json();
        const reply = json.reply || "⚠️ No response";

        setHistory((h) => [...h, { role: "assistant", text: reply }]);
      } else {
        // 🔹 If text, use chat API
        const body = {
          messages: [{ role: "user", parts: [{ text: userMessage.text }] }],
          prompt: userMessage.text,
        };

        const resp = await fetch("http://localhost:5000/api/chat", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body),
        });

        const json = await resp.json();
        const reply = json.reply || "⚠️ No response";

        setHistory((h) => [...h, { role: "assistant", text: reply }]);
      }
    } catch (err) {
      setHistory((h) => [
        ...h,
        { role: "assistant", text: "❌ Error: " + err.message },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      send();
    }
  };

  return (
    <div className="app-container">
      {/* 🔹 Sidebar for history */}
      <div className="sidebar">
        <h3>📜 History</h3>
        {allHistory.length === 0 && (
          <div className="placeholder">No history yet</div>
        )}
        <ul>
          {allHistory.map((h, i) => (
            <li key={i} className="history-item">
              <b>{h.type === "image" ? "🖼️" : "💬"}</b> {h.userMessage}
              <div className="history-reply">↳ {h.botReply}</div>
            </li>
          ))}
        </ul>
      </div>

      {/* 🔹 Main Chat Section */}
      <div className="chat-container">
        <h2 className="title">Devbot</h2>

        <div className="chat-box">
          {history.length === 0 && (
            <div className="placeholder">Say hi 👋</div>
          )}
          {history.map((m, i) => (
            <div key={i} className={`message ${m.role}`}>
              {m.image && (
                <img
                  src={URL.createObjectURL(m.image)}
                  alt="upload"
                  className="chat-image"
                />
              )}
              <div className="bubble">{m.text}</div>
            </div>
          ))}
          {loading && <div className="loading">⏳ Thinking...</div>}
        </div>

        <div className="input-area">
          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Type a message..."
          />
          <div className="actions">
            <button
              className="upload-btn"
              onClick={() => fileInputRef.current.click()}
            >
              📎
            </button>
            <input
              type="file"
              ref={fileInputRef}
              style={{ display: "none" }}
              accept="image/*"
              onChange={(e) => setImage(e.target.files[0])}
            />
            <button className="send-btn" onClick={send} disabled={loading}>
              {loading ? "..." : "➤"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
