import React, { useState, useRef } from "react";
import "./App.css"; // ‚úÖ custom CSS

export default function App() {
  const [input, setInput] = useState("");
  const [history, setHistory] = useState([]); // {role, text, image?}
  const [loading, setLoading] = useState(false);
  const [image, setImage] = useState(null);
  const fileInputRef = useRef();

  const send = async () => {
    if (!input.trim() && !image) return;

    const userMessage = { role: "user", text: input.trim(), image };
    setHistory((h) => [...h, userMessage]);

    setInput("");
    setImage(null);
    setLoading(true);

    try {
      const body = {
        messages: [{ role: "user", parts: [{ text: userMessage.text }] }],
      };

      const resp = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      const json = await resp.json();
      const reply = json.reply || "‚ö†Ô∏è No response";

      setHistory((h) => [...h, { role: "assistant", text: reply }]);
    } catch (err) {
      setHistory((h) => [
        ...h,
        { role: "assistant", text: "‚ùå Error: " + err.message },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      send();
    }
  };

  return (
    <div className="chat-container">
      <h2 className="title">Devbot</h2>

      <div className="chat-box">
        {history.length === 0 && (
          <div className="placeholder">Say hi üëã</div>
        )}
        {history.map((m, i) => (
          <div key={i} className={`message ${m.role}`}>
            {m.image && (
              <img
                src={URL.createObjectURL(m.image)}
                alt="upload"
                className="chat-image"
              />
            )}
            <div className="bubble">{m.text}</div>
          </div>
        ))}
        {loading && <div className="loading">‚è≥ Thinking...</div>}
      </div>

      <div className="input-area">
        <textarea
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Type a message..."
        />
        <div className="actions">
          <button
            className="upload-btn"
            onClick={() => fileInputRef.current.click()}
          >
            üìé
          </button>
          <input
            type="file"
            ref={fileInputRef}
            style={{ display: "none" }}
            accept="image/*"
            onChange={(e) => setImage(e.target.files[0])}
          />
          <button className="send-btn" onClick={send} disabled={loading}>
            {loading ? "..." : "‚û§"}
          </button>
        </div>
      </div>
    </div>
  );
}
