import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "./Sidebar";
import "./Style.css";

export default function Chat() {
  const [input, setInput] = useState("");
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [image, setImage] = useState(null);
  const fileInputRef = useRef();
  const navigate = useNavigate();

  const token = localStorage.getItem("token");

  // ✅ Redirect to login only once on mount
  useEffect(() => {
    if (!token || token === "undefined" || token === "null") {
      navigate("/login", { replace: true });
    }
  }, []); // empty dependency array -> no infinite loop

  // ✅ Fetch chat history only if token is available
  useEffect(() => {
    const fetchHistory = async () => {
      if (!token) return;

      try {
        const resp = await fetch("http://localhost:5000/api/history", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await resp.json();

        // Make sure data is an array
        setHistory(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error("Error fetching history:", err);
      }
    };

    fetchHistory();
  }, [token]);

  // ✅ Send message or image
  const send = async () => {
    if (!input.trim() && !image) return;

    const userMessage = { role: "user", text: input.trim(), image };
    setHistory((h) => [...h, userMessage]);

    setInput("");
    setImage(null);
    setLoading(true);

    try {
      let reply = "⚠️ No response";

      if (image) {
        const formData = new FormData();
        formData.append("image", image);
        formData.append("prompt", input || "Describe this image");

        const resp = await fetch("http://localhost:5000/api/upload", {
          method: "POST",
          headers: { Authorization: `Bearer ${token}` },
          body: formData,
        });

        const json = await resp.json();
        reply = json.reply || reply;
      } else {
        const body = { prompt: userMessage.text };

        const resp = await fetch("http://localhost:5000/api/chat", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(body),
        });

        const json = await resp.json();
        reply = json.reply || reply;
      }

      setHistory((h) => [...h, { role: "assistant", text: reply }]);
    } catch (err) {
      setHistory((h) => [
        ...h,
        { role: "assistant", text: "❌ Error: " + err.message },
      ]);
    } finally {
      setLoading(false);
    }
  };

  // ✅ Handle Enter key to send message
  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      send();
    }
  };

  // ✅ Logout handler
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login", { replace: true });
  };

  return (
    <div className="app-container">
      <Sidebar allHistory={history} />

      <div className="chat-container">
        <div className="chat-header">
          <div className="heading">
          <h2 className="title">Devbot</h2>
          <button onClick={handleLogout} className="logout-btn">
            🚪 Logout
          </button>
          </div>
        </div>

        <div className="chat-box">
          {history.length === 0 && <div className="placeholder">Say hi 👋</div>}
          {history.map((m, i) => (
            <div key={i} className={`message ${m.role}`}>
              {m.image && typeof m.image === "string" && (
                <img src={m.image} alt="upload" className="chat-image" />
              )}
              {m.image && m.image instanceof File && (
                <img
                  src={URL.createObjectURL(m.image)}
                  alt="upload"
                  className="chat-image"
                />
              )}
              <div className="bubble">
                {m.text || m.userMessage || m.botReply}
              </div>
            </div>
          ))}
          {loading && <div className="loading">⏳ Thinking...</div>}
        </div>

        <div className="input-area">
          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Type a message..."
          />
          <div className="actions">
            <button
              className="upload-btn"
              onClick={() => fileInputRef.current.click()}
            >
              📎
            </button>
            <input
              type="file"
              ref={fileInputRef}
              style={{ display: "none" }}
              accept="image/*"
              onChange={(e) => setImage(e.target.files[0])}
            />
            <button className="send-btn" onClick={send} disabled={loading}>
              {loading ? "..." : "➤"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
