// Sidebar.jsx
import React from "react";
import "./Style.css";

export default function Sidebar({ allHistory }) {
  return (
    <div className="sidebar">
      <h3 className="sidebar-title">ðŸ“œ History</h3>

      <div className="sidebar-list">
        {allHistory.length === 0 ? (
          <p className="no-history">No chats yet</p>
        ) : (
          allHistory.map((msg, i) => (
            <div key={i} className={`sidebar-item ${msg.role}`}>
              {/* Image check */}
              {msg.image ? (
                <span>ðŸ“Ž Image</span>
              ) : (
                <span>
                  {msg.text && msg.text.length > 25
                    ? msg.text.substring(0, 25) + "..."
                    : msg.text}
                </span>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
}
