// Chat.jsx
import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "./Sidebar";
import "./Style.css";

export default function Chat() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([]); // Session-only messages
  const [history, setHistory] = useState([]);   // Saved history from backend
  const [loading, setLoading] = useState(false);
  const [image, setImage] = useState(null);
  const fileInputRef = useRef();
  const navigate = useNavigate();

  const token = localStorage.getItem("token");

  // Redirect to login if no token
  useEffect(() => {
    if (!token || token === "undefined" || token === "null") {
      navigate("/login", { replace: true });
    }
  }, []);

  // Fetch chat history from backend
  useEffect(() => {
  const fetchHistory = async () => {
    if (!token) return;

    try {
      const resp = await fetch("http://localhost:5000/api/history", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await resp.json();

      // Format saved messages into role-based structure
      const loadedMessages = data.flatMap((entry) => {
        const msgs = [];
        if (entry.userMessage) {
          msgs.push({ role: "user", text: entry.userMessage, image: entry.image });
        }
        if (entry.botReply) {
          msgs.push({ role: "assistant", text: entry.botReply });
        }
        return msgs;
      });

      setMessages(loadedMessages); // load into main chat
      setHistory(data);            // also show in sidebar
    } catch (err) {
      console.error("Error fetching history:", err);
    }
  };

  fetchHistory();
}, [token]);

  // Send message or image
  const send = async () => {
    if (!input.trim() && !image) return;

    const userMessage = { role: "user", text: input.trim(), image };
    setMessages((m) => [...m, userMessage]);

    setInput("");
    setImage(null);
    setLoading(true);

    try {
      let reply = "‚ö†Ô∏è No response";

      if (image) {
        const formData = new FormData();
        formData.append("image", image);
        formData.append("prompt", input || "Describe this image");

        const resp = await fetch("http://localhost:5000/api/upload", {
          method: "POST",
          headers: { Authorization: `Bearer ${token}` },
          body: formData,
        });

        const json = await resp.json();
        reply = json.reply || reply;
      } else {
        const body = { prompt: userMessage.text };

        const resp = await fetch("http://localhost:5000/api/chat", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(body),
        });

        const json = await resp.json();
        reply = json.reply || reply;
      }

      setMessages((m) => [...m, { role: "assistant", text: reply }]);
    } catch (err) {
      setMessages((m) => [
        ...m,
        { role: "assistant", text: "‚ùå Error: " + err.message },
      ]);
    } finally {
      setLoading(false);
    }
  };

  // Handle Enter key to send
  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      send();
    }
  };

  // ‚úÖ Save current chat to backend
  const saveChat = async () => {
    if (!messages.length) return;

    try {
      const resp = await fetch("http://localhost:5000/api/save", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ messages }),
      });

      const data = await resp.json();

      if (data.success) {
        alert("‚úÖ Chat saved to history!");

        // Update sidebar history with saved messages
        setHistory((prev) => [...prev, ...messages]);

        // Clear current chat session
        setMessages([]);
      } else {
        alert("‚ùå Failed to save chat.");
      }
    } catch (err) {
      console.error("Error saving chat:", err);
      alert("‚ùå Error while saving chat.");
    }
  };

  return (
    <div className="app-container">
      <Sidebar allHistory={history} />

      <div className="chat-container">
        <div className="chat-header">
          <h2 className="title">Devbot</h2>
        </div>

        <div className="chat-box">
          {messages.length === 0 && <div className="placeholder">Say hi üëã</div>}
          {messages.map((m, i) => (
            <div key={i} className={`message ${m.role}`}>
              {m.image && typeof m.image === "string" && (
                <img src={m.image} alt="upload" className="chat-image" />
              )}
              {m.image && m.image instanceof File && (
                <img
                  src={URL.createObjectURL(m.image)}
                  alt="upload"
                  className="chat-image"
                />
              )}
              <div className="bubble">
                {m.text || m.userMessage || m.botReply}
              </div>
            </div>
          ))}
          {loading && <div className="loading">‚è≥ Thinking...</div>}
        </div>

        <div className="input-area">
          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Type a message..."
          />
          <div className="actions">
            <button
              className="upload-btn"
              onClick={() => fileInputRef.current.click()}
            >
              üìé
            </button>
            <input
              type="file"
              ref={fileInputRef}
              style={{ display: "none" }}
              accept="image/*"
              onChange={(e) => setImage(e.target.files[0])}
            />
            <button className="send-btn" onClick={send} disabled={loading}>
              {loading ? "..." : "‚û§"}
            </button>
          </div>
        </div>
        {/* ‚úÖ Save Chat Button */}
          <button
            className="save-chat-btn"
            onClick={saveChat}
            disabled={messages.length === 0}
          >
            üíæ Save Chat
          </button>
      </div>
    </div>
  );
}
