import React from "react";
import { useNavigate } from "react-router-dom";
import "./Style.css";

export default function Sidebar({ allHistory }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login", { replace: true });
  };

  // âœ… Group messages into conversation pairs (userMessage + botReply)
  const groupedConversations = [];

  for (let i = 0; i < allHistory.length; i++) {
    const msg = allHistory[i];

    if (msg.userMessage) {
      const botReply = allHistory[i + 1]?.botReply || null;

      groupedConversations.push({
        userMessage: msg.userMessage,
        botReply,
        image: msg.image,
        createdAt: msg.createdAt,
      });

      i++; // Skip next entry (bot reply already paired)
    }
  }

  return (
    <div className="sidebar">
      <h3 className="sidebar-title">ðŸ“œ History</h3>

      <div className="sidebar-list">
        {groupedConversations.length === 0 ? (
          <p className="no-history">No chats yet</p>
        ) : (
          groupedConversations.map((conv, i) => (
            <div key={i} className="sidebar-item">
              {conv.image ? (
                <span>ðŸ“Ž Image Chat</span>
              ) : (
                <span>
                  {conv.userMessage.length > 25
                    ? conv.userMessage.slice(0, 25) + "..."
                    : conv.userMessage}
                </span>
              )}
              <div className="timestamp">
                {new Date(conv.createdAt).toLocaleString()}
              </div>
            </div>
          ))
        )}
      </div>

      <div className="sidebar-footer">
        <button onClick={handleLogout} className="logout-btn">
          ðŸšª Logout
        </button>
      </div>
    </div>
  );
}
